package LeetCode;

public class ShortestPalindrome {
	/*
	 Given "aacecaaa", return "aaacecaaa".
	 
	 aacecaaa
	 a - 5 
	 c - 2
	 e - 1
	 
	 acacaea
	 
	 aaaceca a acecaaa

Given "abcd", return "dcbabcd".
	 */
	public static void main(String[] args) {
//		String inp = "";
		String inp = "abcd";
		System.out.println(new ShortestPalindrome().shortPali(inp));
	}
	
	public String shortPali(String s){
		if (s.length() <= 1 || isPali2(s))
			return s;
		
//		abc
//	abbc cbbabbc
		
		// /ab ad
		// abcbe
		int center = s.length()/2-1;
		while(center >= 0){
			StringBuilder sb = new StringBuilder(s.substring(center+ center+1));
			String temp = sb.reverse().toString()+s;
			if(isPali2(temp))
				return temp;
			center--;
		}
		
		
		return null;
	}
	
	private boolean isPali2(String str) {
		
		int s = 0, e = str.length() - 1;
		if(str.length() %2 == 0){
			s = str.length()/2-1;
			e = str.length()/2;
		}else{
			s = str.length()/2-1;
			e = str.length()/2+1;
		}
		while (s  >=0 &&  e<str.length()) {
			if (str.charAt(s) != str.charAt(e)) {
				return false;
			}
			s--;
			e++;
		}
		return true;
	}
	
	
	public String shortestPalindrome(String s) {
		if (s.length() <= 1 || isPali(s))
			return s;
		StringBuilder sb = new StringBuilder(s.substring(1));
		int start = sb.length() - 1;
		int [] count = init(s);
		s = sb.reverse().toString() + s;
		
		while (start >= 0) {
			String temp = s.substring(start);
			count[s.charAt(start)-'a']++;
			//isPali(count) && 
			if (isPali(temp)) {
				return temp;
			}
			start--;
		}
		return s;
	}
	/*
	 abad
	 
	 dababad
	 
	 dabad
	 */
	private boolean isPali(int [] array){
		int odd = 0;
		for(int i=0;i<array.length;i++){
			if(array[i]%2!=0)
				odd++;
		}
		if(odd > 1) return false;
		return true;
	}

	private int[] init(String str) {
		int []count = new int[26];
		for(int i=0;i<str.length();i++)
			count[str.charAt(i)-'a']++;
		return count;
	}
	private boolean isPali(String str) {
		int s = 0, e = str.length() - 1;
		while (s < e) {
			if (str.charAt(s) != str.charAt(e)) {
				return false;
			}
			s++;
			e--;
		}
		return true;
	}
}
